import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

const QuizContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  min-height: calc(100vh - 70px);
`;

const Header = styled.header`
  text-align: center;
  margin-bottom: 30px;
  color: white;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 20px;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 8px;
  background: rgba(255,255,255,0.3);
  border-radius: 4px;
  overflow: hidden;
  margin-bottom: 15px;
`;

const Progress = styled.div`
  height: 100%;
  background: #4CAF50;
  transition: width 0.3s ease;
  width: ${props => props.width}%;
`;

const QuestionCounter = styled.div`
  font-size: 1.2rem;
  font-weight: 600;
`;

const QuestionCard = styled.div`
  background: white;
  border-radius: 15px;
  padding: 30px;
  box-shadow: 0 10px 30px rgba(0,0,0,0.2);
  margin-bottom: 20px;
`;

const QuestionText = styled.h2`
  font-size: 1.4rem;
  line-height: 1.6;
  margin-bottom: 25px;
  color: #2c3e50;
`;

const AnswersContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
  margin-bottom: 30px;
`;

const AnswerOption = styled.div`
  background: #f8f9fa;
  border: 2px solid #e9ecef;
  border-radius: 10px;
  padding: 15px 20px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 1.1rem;
  
  &:hover {
    background: #e3f2fd;
    border-color: #2196F3;
    transform: translateY(-2px);
  }
  
  &.selected {
    background: #e3f2fd;
    border-color: #2196F3;
    color: #1976D2;
    font-weight: 600;
  }
  
  &.correct {
    background: #e8f5e8;
    border-color: #4CAF50;
    color: #2e7d32;
  }
  
  &.incorrect {
    background: #ffebee;
    border-color: #f44336;
    color: #c62828;
  }
  
  &.disabled {
    cursor: not-allowed;
    opacity: 0.7;
  }
`;

const Controls = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 15px;
  
  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const Button = styled.button`
  padding: 12px 24px;
  border: none;
  border-radius: 8px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  
  &.primary {
    background: #2196F3;
    color: white;
    
    &:hover:not(:disabled) {
      background: #1976D2;
      transform: translateY(-2px);
    }
  }
  
  &.secondary {
    background: #6c757d;
    color: white;
    
    &:hover:not(:disabled) {
      background: #5a6268;
      transform: translateY(-2px);
    }
  }
  
  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
  }
  
  @media (max-width: 768px) {
    width: 100%;
  }
`;

const Feedback = styled.div`
  margin-top: 15px;
  padding: 15px;
  border-radius: 8px;
  font-weight: 600;
  
  &.correct {
    background: #e8f5e8;
    color: #2e7d32;
    border: 1px solid #4CAF50;
  }
  
  &.incorrect {
    background: #ffebee;
    color: #c62828;
    border: 1px solid #f44336;
  }
`;

const Loading = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 200px;
  color: white;
  font-size: 1.2rem;
`;

function Quiz() {
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState([]);
  const [isAnswered, setIsAnswered] = useState(false);
  const [feedback, setFeedback] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchQuestions();
  }, []);

  const fetchQuestions = async () => {
    try {
      const response = await axios.get('/api/questions');
      setQuestions(response.data.data);
      setUserAnswers(new Array(response.data.data.length).fill(null));
      setLoading(false);
    } catch (error) {
      console.error('Lỗi khi tải câu hỏi:', error);
      setLoading(false);
    }
  };

  const selectAnswer = (answerIndex) => {
    if (isAnswered) return;
    
    const newAnswers = [...userAnswers];
    newAnswers[currentQuestionIndex] = answerIndex;
    setUserAnswers(newAnswers);
  };

  const checkAnswer = async () => {
    if (userAnswers[currentQuestionIndex] === null) {
      alert('Vui lòng chọn một đáp án!');
      return;
    }

    try {
      const response = await axios.post('/api/submit-answer', {
        questionId: questions[currentQuestionIndex].id,
        selectedAnswer: userAnswers[currentQuestionIndex]
      });

      setFeedback(response.data.data);
      setIsAnswered(true);
    } catch (error) {
      console.error('Lỗi khi kiểm tra đáp án:', error);
    }
  };

  const nextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setIsAnswered(false);
      setFeedback(null);
    }
  };

  const previousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
      setIsAnswered(false);
      setFeedback(null);
    }
  };

  const finishQuiz = async () => {
    const answers = questions.map((question, index) => ({
      questionId: question.id,
      selectedAnswer: userAnswers[index]
    }));

    try {
      const response = await axios.post('/api/submit-quiz', { answers });
      navigate('/results', { state: { results: response.data.data } });
    } catch (error) {
      console.error('Lỗi khi nộp bài:', error);
    }
  };

  if (loading) {
    return (
      <QuizContainer>
        <Loading>
          <div className="spinner"></div>
          <span style={{ marginLeft: '10px' }}>Đang tải câu hỏi...</span>
        </Loading>
      </QuizContainer>
    );
  }

  if (questions.length === 0) {
    return (
      <QuizContainer>
        <QuestionCard>
          <h2>Không có câu hỏi nào</h2>
          <p>Vui lòng tải lên file PDF hoặc liên hệ quản trị viên.</p>
          <Button className="primary" onClick={() => navigate('/')}>
            Về trang chủ
          </Button>
        </QuestionCard>
      </QuizContainer>
    );
  }

  const currentQuestion = questions[currentQuestionIndex];
  const progress = ((currentQuestionIndex + 1) / questions.length) * 100;

  return (
    <QuizContainer>
      <Header>
        <Title>🎓 Ôn Tập Trắc Nghiệm</Title>
        <ProgressBar>
          <Progress width={progress} />
        </ProgressBar>
        <QuestionCounter>
          {currentQuestionIndex + 1} / {questions.length}
        </QuestionCounter>
      </Header>

      <QuestionCard>
        <QuestionText>{currentQuestion.question}</QuestionText>
        
        <AnswersContainer>
          {currentQuestion.options.map((option, index) => (
            <AnswerOption
              key={index}
              className={`
                ${userAnswers[currentQuestionIndex] === index ? 'selected' : ''}
                ${isAnswered && feedback ? (
                  index === feedback.correctAnswer ? 'correct' : 
                  (index === userAnswers[currentQuestionIndex] && !feedback.isCorrect ? 'incorrect' : '')
                ) : ''}
                ${isAnswered ? 'disabled' : ''}
              `}
              onClick={() => selectAnswer(index)}
            >
              {String.fromCharCode(65 + index)}. {option}
            </AnswerOption>
          ))}
        </AnswersContainer>

        {feedback && (
          <Feedback className={feedback.isCorrect ? 'correct' : 'incorrect'}>
            <strong>{feedback.isCorrect ? '✅ Chính xác!' : '❌ Sai rồi!'}</strong><br />
            {feedback.explanation}
          </Feedback>
        )}

        <Controls>
          <Button 
            className="secondary" 
            onClick={previousQuestion}
            disabled={currentQuestionIndex === 0}
          >
            ← Câu trước
          </Button>
          
          {!isAnswered ? (
            <Button className="primary" onClick={checkAnswer}>
              Kiểm tra đáp án
            </Button>
          ) : (
            currentQuestionIndex === questions.length - 1 ? (
              <Button className="primary" onClick={finishQuiz}>
                Hoàn thành
              </Button>
            ) : (
              <Button className="primary" onClick={nextQuestion}>
                Câu tiếp →
              </Button>
            )
          )}
          
          <Button 
            className="secondary" 
            onClick={nextQuestion}
            disabled={currentQuestionIndex === questions.length - 1}
          >
            Câu tiếp →
          </Button>
        </Controls>
      </QuestionCard>
    </QuizContainer>
  );
}

export default Quiz;
